 SELECT logged_actions.event_id,
    logged_actions.row_data AS previa,
    logged_actions.changed_fields AS actual
   FROM audit.logged_actions
  WHERE (logged_actions.event_id = ( SELECT max(logged_actions_1.event_id) AS max
