--
-- Name: vcarnet; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.vcarnet AS
 SELECT carnets.id_carnet,
    ('MINPI-CN-'::text || (carnets.carnet)::text) AS carnet,
        CASE
            WHEN (carnets.id_status = 1) THEN 'DISPONIBLE'::text
            WHEN (carnets.id_status = 2) THEN 'ASIGNADO'::text
            WHEN (carnets.id_status = 3) THEN 'DESINCORPORADO'::text
            ELSE NULL::text
        END AS status
   FROM public.carnets
  ORDER BY carnets.id_carnet;


ALTER TABLE public.vcarnet OWNER TO postgres;

