--
-- Name: sigesp_personal_activo; Type: VIEW; Schema: sigesp_remoto; Owner: postgres
--

CREATE VIEW sigesp_remoto.sigesp_personal_activo AS
 SELECT b.desuniadm,
    c.cedper,
    ( SELECT DISTINCT (((x.nomper)::text || ' '::text) || (x.apeper)::text)
           FROM sigesp_remoto.sno_personal x
          WHERE (x.codper = a.codper)) AS nombre
   FROM ((sigesp_remoto.sno_hpersonalnomina a
     LEFT JOIN sigesp_remoto.sno_unidadadmin b ON (((b.prouniadm = a.prouniadm) AND (b.minorguniadm = a.minorguniadm))))
     LEFT JOIN sigesp_remoto.sno_personal c ON ((c.codper = a.codper)))
  WHERE ((a.codperi IN ( SELECT max(x.codperi) AS max
           FROM sigesp_remoto.sno_hpersonalnomina x)) AND ((a.codnom)::integer <= 5009) AND (((a.minorguniadm)::integer = 0) AND ((a.ofiuniadm)::integer = 0) AND ((a.depuniadm)::integer = 0) AND ((a.prouniadm)::integer < 20)) AND ((a.staper)::integer = 1))
  ORDER BY (c.cedper)::integer;


