-- Name: voficina_trabajador; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.voficina_trabajador AS
 SELECT x.id_oficina,
    x.desuniadm,
    x.cedper,
    x.nombre
   FROM ( SELECT b.id_oficina,
            1 AS desuniadm,
            a.cedper,
            a.nombre
           FROM (sigesp_remoto.sigesp_personal_activo a
             LEFT JOIN public.oficinas b ON (((b.oficina)::text = (a.desuniadm)::text)))
        UNION
         SELECT c.id_oficina,
            c.id_empresa,
            (c.ci_trabajador)::text AS ci_trabajador,
            c.nombre_trabajador
           FROM public.trabajadores c
          WHERE c.activo) x
  ORDER BY x.desuniadm, x.id_oficina, x.cedper;


