 SELECT a.ci_visitante,
    b.nombre_visitante,
    round((sum(date_part('epoch'::text, age(a.fecha_hora_salida, (a.fechahora_ingreso)::timestamp with time zone))))::numeric, 2) AS duracion_segundos,
    round(((sum(date_part('epoch'::text, age(a.fecha_hora_salida, (a.fechahora_ingreso)::timestamp with time zone))) / (60)::double precision))::numeric, 2) AS duracion_minutos,
    round(((sum(date_part('epoch'::text, age(a.fecha_hora_salida, (a.fechahora_ingreso)::timestamp with time zone))) / ((60 * 60))::double precision))::numeric, 2) AS duracion_horas,
    round(((sum(date_part('epoch'::text, age(a.fecha_hora_salida, (a.fechahora_ingreso)::timestamp with time zone))) / (((60 * 60) * 24))::double precision))::numeric, 2) AS duracion_dias,
    round(((sum(date_part('epoch'::text, age(a.fecha_hora_salida, (a.fechahora_ingreso)::timestamp with time zone))) / ((((60 * 60) * 24) * 30))::double precision))::numeric, 2) AS duracion_meses
   FROM (public.visitas a
     LEFT JOIN public.visitantes b ON ((b.ci_visitante = a.ci_visitante)))
  GROUP BY a.ci_visitante, b.nombre_visitante
  ORDER BY a.ci_visitante;


