--
-- Name: sigesp_personal_activo; Type: VIEW; Schema: sigesp_remoto; Owner: postgres
--

CREATE VIEW sigesp_remoto.sigesp_personal_activo AS
 SELECT b.desuniadm,
    c.cedper,
    (((c.nomper)::text || ' '::text) || (c.apeper)::text) AS nombre
   FROM ((sigesp_remoto.sno_personalnomina a
     LEFT JOIN sigesp_remoto.sno_unidadadmin b ON ((b.prouniadm = a.prouniadm)))
     LEFT JOIN sigesp_remoto.sno_personal c ON ((c.codper = a.codper)))
  WHERE (((a.staper)::integer = 1) AND (((b.desuniadm)::text !~~ 'D.G.T%'::text) OR ((b.desuniadm)::text ~~ '%ZONAS URBANAS%'::text)))
  ORDER BY b.desuniadm, c.nomper;


