-- Name: vvisitantes; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.vvisitantes AS
 SELECT a.id_visita,
    a.ci_visitante,
    b.carnet,
    to_char(a.fechahora_ingreso, 'dd/MM/yyyy HH12:MI:SS AM'::text) AS fechahora_ingreso,
    to_char(a.fecha_hora_salida, 'dd/MM/yyyy HH12:MI:SS AM'::text) AS fecha_hora_salida,
        CASE
            WHEN a.salida THEN 'SI'::text
            ELSE 'NO'::text
        END AS salida,
    c.nombre_visitante,
    c.telefono_visitante,
    d.nombre_pueblo,
    e.entidad,
    f.procedencia,
    g.tipo_visitante,
    h.motivo,
    k.nombre AS autorizado,
    i.piso,
    j.destino,
    a.observaciones
   FROM ((((((((((public.visitas a
     LEFT JOIN public.carnets b ON ((b.id_carnet = a.id_carnet)))
     LEFT JOIN public.visitantes c ON ((c.ci_visitante = a.ci_visitante)))
     LEFT JOIN public.pueblos d ON ((d.id_pueblo = a.id_pueblo)))
     LEFT JOIN public.entidades e ON ((e.id_entidad = a.id_entidad)))
     LEFT JOIN public.procedencias f ON ((f.id_procedencia = a.id_procedencia)))
     LEFT JOIN public.tipo_visitante g ON ((g.id_tipo_visitante = a.id_tipo_visitante)))
     LEFT JOIN public.motivos h ON ((h.id_motivo = a.id_motivo)))
     LEFT JOIN public.pisos i ON ((i.id_piso = a.id_piso)))
     LEFT JOIN public.destinos j ON ((j.id_destino = a.id_destino)))
     LEFT JOIN public.voficina_trabajador k ON (((k.cedper)::integer = a.ci_autorizacion)))
  ORDER BY a.fechahora_ingreso DESC;


