-- Name: pisos_oficinas_destinos; Type: VIEW; Schema: sigesp_remoto; Owner: postgres
--

CREATE VIEW sigesp_remoto.pisos_oficinas_destinos AS
 SELECT c.piso,
    b.desuniadm,
    a.destino
   FROM ((public.destinos a
     LEFT JOIN sigesp_remoto.sigesp_oficinas b ON ((b.prouniadm = a.id_oficina)))
     LEFT JOIN public.pisos c ON ((c.id_piso = a.id_piso)));


